import React, {useEffect, useState} from "react";
import {useNavigate, useNavigation, useParams} from "react-router-dom";

import {apiGet, apiPost, apiPut} from "../utils/api";

import InputField from "../components/InputField";
import FlashMessage from "../components/FlashMessage";


const InvoiceForm = () => {
    const Navigate = useNavigation();
    const {id} = useParams();
    const[invoice, setInvoice] = useState({
        invoiceNumber: "",
        seller: " ",
        buyer: " ",
        issued: "",
        dueDate: "",
        product: "",
        price: "",
        vat: "",
        note: ""
    });
    const [sentState, setSent] = useState(false);
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);

    useEffect(() => {
        if (id) {
            apiGet("api/invoices/" + id).then((data) => setInvoice(data))
    }
     }, [id]);

    const handleSumit = (e) => {
        e.preventDefault();

        (id ? apiPut("api/invoices/" + id, invoice): apiPost("/api/invoices", invoice))
        .then((data)=> {
            setSent(true);
            setSuccess(true);
            Navigate("/invoices");
        })
        .catch((error) => {
            console.log(error.message);
            setError(error.message);
            setSent(true);
            setSuccess(false);
        });


    };

    const sent = sentState;
    const success = successState;
    return (
        <div>
            <h1>{id ? "Upravit" : "Vytvořit"} Faktura</h1>
            <hr/>
            {errorState ? (
                <div className="alert alert-danger">{errorState}</div>
            ) : null}
            {sent && (
                <FlashMessage
                    theme={success ? "success" : ""}
                    text={success ? "Uložení faktury proběhlo úspěšně." : ""}
                />
            )}
             <form onSubmit={handleSubmit}>
           

                <InputField
                    required={true}
                    type="text"
                    name="invoiceNumber"
                    min="3"
                    label="ID"
                    prompt="Zadejte invoiceNumber"
                    value={invoice.invoiceNumber}
                    handleChange={(e) => {
                        setInvoice({...invoice, invoiceNumber: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="issued"
                    min="3"
                    label="issued"
                    prompt="Zadejte datum vystavení"
                    value={invoice.issued}
                    handleChange={(e) => {
                        setInvoice({...invoice, issued: e.target.value});
                    }}
                />

                odběratel, dodavatel

                <InputField
                    required={true}
                    type="text"
                    name="buyer"
                    min="3"
                    label="buyer"
                    prompt="Zadejte odběratele"
                    value={invoice.buyer}
                    handleChange={(e) => {
                        setInvoice({...invoice, buyer: e.target.value});
                    }}
                />
                 <InputField
                    required={true}
                    type="text"
                    name="seller"
                    min="3"
                    label="seller"
                    prompt="Zadejte dodavatele"
                    value={invoice.seller}
                    handleChange={(e) => {
                        setInvoice({...invoice, seller: e.target.value});
                    }}
                />



                <InputField
                    required={true}
                    type="text"
                    name="dueDate"
                    min="3"
                    label="dueDate"
                    prompt="Zadejte datum splatnosti"
                    value={invoice.dueDate}
                    handleChange={(e) => {
                        setInvoice({...invoice, dueDate: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="product"
                    min="3"
                    label="product"
                    prompt="Zadejte název produktu"
                    value={invoice.product}
                    handleChange={(e) => {
                        setInvoice({...invoice, bankCode: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="price"
                    min="3"
                    label="price"
                    prompt="Zadejte cenu"
                    value={invoice.price}
                    handleChange={(e) => {
                        setInvoice({...invoice, iban: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="vat"
                    min="3"
                    label="vat"
                    prompt="Zadejte daň"
                    value={invoice.vat}
                    handleChange={(e) => {
                        setInvoice({...invoice, telephone: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="note"
                    min="3"
                    label="note"
                    prompt="Zadejte poznámku"
                    value={invoice.note}
                    handleChange={(e) => {
                        setInvoice({...invoice, mail: e.target.value});
                    }}
                />

                

                <input type="submit" className="btn btn-primary" value="Uložit"/>
            </form> 
            </div>
    );
};

export default InvoiceForm;
