import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";

import {apiGet} from "../utils/api";
import Country from "./Country";

const PersonDetail = () => {
 const {id} = useParams();
 const [person, setPerson] = useState({});
 const [purchases, setPurchases] = useState([]);
 const [sales, setSales] = useState([]);

 useEffect(() => {
 const fetchData = async () => {

 const personData = await apiGet("/api/persons/" + id);
 setPerson(personData);

 const purchasesData = await apiGet("/api/identification/" + personData.identificationNumber + "/purchases");
 setPurchases(purchasesData);

 const salesData = await apiGet("/api/identification/" + personData.identificationNumber + "/sales");
 setSales(salesData); }
 fetchData(); 
 }, [id]);
 const country = Country.CZECHIA === person.country ? "Česká republika" : "Slovensko";

 return (
 <>
 <div>
 <h1>Detail osoby</h1>
 <hr />
 <h3>{person.name} ({person.identificationNumber})</h3>
 <p>
 <strong>DIČ:</strong>
 <br />
 {person.taxNumber}
 </p>
 <p>
 <strong>Bankovní účet:</strong>
 <br />
 {person.accountNumber}/{person.bankCode} ({person.iban})
 </p>
 <p>
 <strong>Tel.:</strong>
 <br />
 {person.telephone}
 </p>
 <p>
 <strong>Mail:</strong>
 <br />
 {person.mail}
 </p>
 <p>
 <strong>Sídlo:</strong>
 <br />
 {person.street}, {person.city},
 {person.zip}, {country}
 </p>
 <p>
 <strong>Poznámka:</strong>
 <br />
 {person.note}
 </p>
 </div>

 <div>
 <h2>Přijaté faktury konkrétní firmou</h2>
 <table className="table table-bordered">
 <thead>
 <tr>
 <th>Číslo faktury</th>
 <th>Datum vystavení</th>
 <th>Datum splatnosti</th>
 <th>Název produktu</th>
 <th>Cena</th>
 <th>VAT</th>
 <th>Poznámka</th>
 </tr>
 </thead>
 <tbody>
 {purchases.map((purchase) => (
 <tr key={purchase.id}>
 <td>{purchase.invoiceNumber}</td>
 <td>{purchase.issued}</td>
 <td>{purchase.dueDate}</td>
 <td>{purchase.product}</td>
 <td>{purchase.price}</td>
 <td>{purchase.vat}</td>
 <td>{purchase.note}</td>
 </tr>
 ))}
 </tbody>
 </table>
 </div>

 <div>
 <h2>Vystavené faktury konkrétní firmou</h2>
 <table className="table table-bordered">
 <thead>
 <tr>
 <th>Číslo faktury</th>
 <th>Datum vystavení</th>
 <th>Datum splatnosti</th>
 <th>Název produktu</th>
 <th>Cena</th>
 <th>VAT</th>
 <th>Poznámka</th>
 </tr>
 </thead>
 <tbody>
 {sales.map((sale) => (
 <tr key={sale.id}>
 <td>{sale.invoiceNumber}</td>
 <td>{sale.issued}</td>
 <td>{sale.dueDate}</td>
 <td>{sale.product}</td>
 <td>{sale.price}</td>
 <td>{sale.vat}</td>
 <td>{sale.note}</td>
 </tr>
 ))}
 </tbody>
 </table>
 </div>
 </>
 );
};

export default PersonDetail;
